import React from 'react';
import Contact from "./Contact"

const ContactList = ({ data, currentPage, getAllContacts }) => {
    return (
        <main className='main'>
            {data?.content?.length === 0 && <div>No Contacts. Please add a new contact</div>}

            <ul className='contact__list'>
                {data?.content?.length > 0 && data.content.map(contact => <Contact contact={contact} key={contact.id} />)}
            </ul>

            {data?.content?.length > 0 && data?.totalPages > 1 &&
                <div className='pagination'>
                    <a onClick={() => currentPage > 0 && getAllContacts(currentPage - 1)}>&laquo;</a>
                    {data?.totalPages > 1 &&
                        [...Array(data.totalPages).keys()].map((page, index) => (
                            <a
                                key={page}
                                onClick={() => getAllContacts(page)}
                                className={currentPage === page ? 'active' : ''}
                            >
                                {page + 1}
                            </a>
                        ))}
                    <a onClick={() => currentPage < data.totalPages - 1 && getAllContacts(currentPage + 1)}>&raquo;</a>
                </div>
            }

        </main>
    )
}

export default ContactList
